import React from 'react';
import Tabletop from 'tabletop';
import SimpleSlider from '../SimpleSlider/SimpleSlider'

// Import css files
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './Home.css';

/* IF CHANGE TO PAPA PARSE IS NECESSARY
CHANGE INIT FUNCTION
    init(){
        Papa.parse('https://docs.google.com/spreadsheets/d/14L2_NpdD9oJaHVeGSBNDnmYqdgmakbbscUIsXP-fUic/pub?output=csv', {
          download: true,
          header: true,
          complete: this.populateData
          }
        )
    }
ADD IMPORT
    import Papa from 'papaparse';
*/

class Home extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            generalData: {
                date: "",
                newInfectionsToday: 0,
                newInfectionsYesterday: 0,
                newInfectionsPercentChange: 0,
                deathsToday: 0,
                deathsTotal: 0,
                testsCompleted: 0,
                testPositivity: 0,
                hospitalized: 0,
                icu: 0,
                icuPercent: 0,
                ventilator: 0,
                ventilatorPercent: 0,
            },
            countyData: {

            }
        }
        this.init = this.init.bind(this);
        this.populateData = this.populateData.bind(this);
    }

    componentDidMount(){
        this.init();
    }

    componentWillUnmount(){

    }

    init(){
        Tabletop.init({
            key: "14L2_NpdD9oJaHVeGSBNDnmYqdgmakbbscUIsXP-fUic",
            simpleSheet: false
        })
        .then((data, tabletop) => this.populateData(data, tabletop))
        .catch((err) => console.warn(err))
    }

    populateData(newData, tabletop){
        var index;
        if(newData.length < 0)
            index = newData.length - 1;
        else
            index = 0;
        console.log(Tabletop.name);
        this.setState({
            generalData: {
                date: newData[index].date,
                newInfectionsToday: newData[index].newInfectionsToday,
                newInfectionsYesterday: newData[index].newInfectionsYesterday,
                newInfectionsPercentChange: newData[index].newInfectionsPercentChange,
                deathsToday: newData[index].deathsToday,
                deathsTotal: newData[index].deathsTotal,
                testsCompleted: newData[index].testsCompleted,
                testsPositivity: newData[index].testsPositivity,
                hospitalized: newData[index].hospitalized,
                icu: newData[index].icu,
                icuPercent: newData[index].icuPercent,
                ventilator: newData[index].ventilator,
                ventilatorPercent: newData[index].ventilatorPercent,
            }
        })
    }

    /*    
    Date: {this.state.generalData.date} <br />
                   New Infections Today:  {this.state.generalData.newInfectionsToday} <br />
                   New Infections Yesterday: {this.state.generalData.newInfectionsYesterday} <br />
                   Increase in New Infections:  {this.state.generalData.newInfectionsPercentChange} <br />
                   Deaths Today: {this.state.generalData.deathsToday} <br />
                   Total Deaths: {this.state.generalData.deathsTotal} <br />
                   Tests Completed: {this.state.generalData.testsCompleted} <br />
                   Test Positivity Rate: {this.state.generalData.testsPositivity} <br />
                   Hospitalized: {this.state.generalData.hospitalized} <br />
                   ICU: {this.state.generalData.icu} <br />
                   ICU Percent: {this.state.generalData.icuPercent} <br />
                   Ventilator: {this.state.generalData.ventilator} <br />
                   Ventilator Percent: {this.state.generalData.ventilatorPercent} <br />
    */
    render(){
        return (
            <div className="container">
                <div className="main-text">
                    <SimpleSlider generalData={this.state.generalData}/>
                </div>
            </div>
        )
    }
}

export default Home;
