import React from 'react';
import Tabletop from 'tabletop';
import './Home.css';

class Home extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            data: {
                date: "",
                dailyCases: 0,
                dailyDeaths: 0,
                dailyCaseIncrease: 0,
                totalCases: 0,
                totalDeaths:0
            }
        }
        this.init = this.init.bind(this);
        this.populateData = this.populateData.bind(this);
    }

    componentDidMount(){
        this.init();
    }

    componentWillUnmount(){

    }

    init(){
        Tabletop.init({
            key: "14L2_NpdD9oJaHVeGSBNDnmYqdgmakbbscUIsXP-fUic",
            simpleSheet: true
        })
        .then((data) => this.populateData(data))
        .catch((err) => console.warn(err))
    }

    populateData(newData){
        console.log(newData);
        this.setState({
            data: {
                date: newData[0].date,
                dailyCases: newData[0].newInfectionsToday,
                dailyDeaths: newData[0].deathsToday,
                dailyCaseIncrease: 0,
                totalCases: 0,
                totalDeaths: 0
            }
        })
    }
    
    render(){
        return (
            <div className="container">
                <div className="main-text">
                    Date: {this.state.data.date}
                    Cases Today: {this.state.data.dailyCases}
                    Case Increase From Yesterday: {this.state.data.dailyCaseIncrease} %
                    Total Cases: {this.state.data.totalCases}
                    Deaths Today: {this.state.data.dailyDeaths}
                    Death Increase From Yesterday: 0%
                    Total Deaths: {this.state.data.totalDeaths}
                </div>
            </div>
        )
    }
}

export default Home;
